import java.util.Scanner;


public class SumTwoNumbers {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,sum;
		Scanner input = new Scanner(System.in);
		a = input.nextInt();
		b = input.nextInt();
		sum = a + b;
		System.out.println("sum = " + sum);

	}
	
}
